import React, { useEffect, useState, useRef } from "react";
import { DownloadTableExcel } from 'react-export-table-to-excel';



const Inventory = () => {
//   const [dd, setDd] = useState([]);
  const [showTable, setShowTable] = useState(false);

  const handleClick = () => {
    setShowTable(true);
  };

  // const getdd = async () => {
  //     const url = "http://ims.hvpn.org.in/IMSAPI/api/Values/GetMemorandumDetail?Key=IMS2022$VENUS@1978";
  //     fetch(url, {
  //       method: "GET",
  //       withCredentials: true,
  //       headers: {
  //         "X-Auth-Token": "ef72570ff371408f9668e414353b7b2e",
  //         "Content-Type": "application/json",
  //         "Access-Control-Allow-Origin" : "http://localhost:3000/"

  //       }
  //     })
  //       .then(resp => resp.json())
  //       .then(function(data) {
  //         console.log(data);
  //       })
  //       .catch(function(error) {
  //         console.log(error);
  //       });
  //     }

  // useEffect(() => {
  //   getdd();
  // }, []);

  const dds = useRef(null);
  const ws = useRef(null);
  const dw = useRef(null);
  const con = useRef(null);


  return (
<>
     
      <h3 align="center" >"Memorandum for Inventory of Dedicated Stores"</h3>
      <h3 align="center">Date of Transaction upto:</h3>
      <div align="center">
      <button align="center" onClick={handleClick}>View Memorandum</button></div>
      



       {/* Table1 */}
       {showTable && (
      <table className="dddd" border="1" align="center" width="90%" ref={dds}>
        
        <thead>
          <tr >
            <th colspan="3" align="left">Status of Inventory of DD Stores as on {dd.sr_no}:</th>
          </tr>
          <tr > 
            <th>Sr. No</th>  
            <th>Description</th>
            <th> In Rs. Lakhs</th>

          </tr>  
              <tr>
                <th>1</th>
              <th> Value of Stock Ending December, 2022</th>
              <th>{dd.dd_store_value_ending_month}</th>
              </tr>

            <tr>  
              <th>2</th>        
            <th>Value of Stock Received During January, 2022 </th>
            <th>{dd.dd_store_value_received}</th>
            </tr>

            <tr>  
              <th>3</th>        
            <th>Value of Stock Issued During January, 2022 </th>
            <th>{dd.dd_store_value_issued}</th>
            </tr>

            <tr>
              <th>4</th>          
            <th>Value of Stock as on  (1+2-3=4) </th>
            <th>{dd.dd_store_value_now}</th>
            </tr>
            </thead>
            <tbody>
            </tbody>
      </table> )}

      {showTable && (<DownloadTableExcel
                    filename="Inventory details on DD Stores"
                    sheet="DD Stores"
                    currentTableRef={dds.current}
                >

                   <button> Export excel </button>

                </DownloadTableExcel>)}
                      {/* Table 2 */}
      <br/><br/>
      {showTable && (<table className="dddd" border="1" align="center" width="90%" ref={ws}>
        
        <thead>
          <tr >
            <th colspan="3" align="left">Status of Inventory of Workshops as on {dd.sr_no}:</th>
          </tr>
          <tr > 
            <th>Sr. No</th>  
            <th>Description</th>
            <th> In Rs. Lakhs</th>

          </tr>  
              <tr>
                <th>1</th>
              <th> Value of Stock Ending December, 2022</th>
              <th>{dd.dd_store_value_ending_month}</th>
              </tr>

            <tr>  
              <th>2</th>        
            <th>Value of Stock Received During January, 2022 </th>
            <th>{dd.dd_store_value_received}</th>
            </tr>

            <tr>  
              <th>3</th>        
            <th>Value of Stock Issued During January, 2022 </th>
            <th>{dd.dd_store_value_issued}</th>
            </tr>

            <tr>
              <th>4</th>          
            <th>Value of Stock as on  (1+2-3=4) </th>
            <th>{dd.dd_store_value_now}</th>
            </tr>
            </thead>
            <tbody>
            </tbody>
      </table>  )}
      {showTable && (<DownloadTableExcel
                    filename="Inventory details on Workshops"
                    sheet="Workshops"
                    currentTableRef={ws.current}
                >

                   <button> Export excel </button>

                </DownloadTableExcel>)}



           {/* Table 3 */}
           <br/>
           {showTable && (<table className="dddd" border="1" align="center" width="90%" ref={dw}>
        
        <thead>
          <tr >
            <th colspan="3" align="left">Status of Inventory of Different Works as on {dd.sr_no}:</th>
          </tr>
          <tr > 
            <th>Sr. No</th>  
            <th>Description</th>
            <th> In Rs. Lakhs</th>

          </tr>  
              <tr>
              <th>1</th>
              <th> Value of Stock Ending December, 2022</th>
              <th>{dd.dd_store_value_ending_month}</th>
              </tr>

            <tr>  
            <th>2</th>        
            <th>Value of Stock Received During January, 2022 </th>
            <th>{dd.dd_store_value_received}</th>
            </tr>

            <tr>  
              <th>3</th>        
            <th>Value of Stock Issued to CWIP and O & M During January, 2022 </th>
            <th>{dd.dd_store_value_issued}</th>
            </tr>

            <tr>
              <th>4</th>          
            <th>Value of Stock as on  (1+2-3=4) </th>
            <th>{dd.dd_store_value_now}</th>
            </tr>
            </thead>
            <tbody>
            </tbody>
      </table>)}
      {showTable && (<DownloadTableExcel
                    filename="Inventory details on Different Works"
                    sheet="Different Works"
                    currentTableRef={dw.current}
                >

                   <button> Export excel </button>

                </DownloadTableExcel>)}


      {/* Table 4 */}
      <br/>
      {showTable && (<table className="dddd" border="1" align="center" width="90%" ref={con}>
        
        <thead>
          <tr><th colspan="4" align="left">Status of Inventory (DD Stores + Workshops + Works) as on {dd.sr_no}:</th></tr>
          <tr > 
            <th>Sr. No</th>  
            <th>Description</th>
            <th> Equipment & Spares etc.</th>
            <th>Scrap</th>
          </tr>
        </thead>
        <tbody>

        <tr>
              <th>1</th>
              <th>  Value of Stock in DD Stores</th>
              <th>{dd.dd_store_value_ending_month}</th>
              <th></th>
              </tr>

            <tr>  
              <th>2</th>        
            <th>Value of Stock in Workshops  </th>
            <th>{dd.dd_store_value_received}</th>
            <th></th>
            </tr>

            <tr>  
            <th>3</th>        
            <th> Value of Stock in Site Stores </th>
            <th>{dd.dd_store_value_issued}</th>
            <th></th>
            </tr>

            <tr>
              <th></th>          
            <th align="right">Total : </th>
            <th>{dd.dd_store_value_now}</th>
            <th></th>
            </tr>
            <tr><th colspan="4">Print Date Time:</th></tr>
         </tbody>
      </table>)}
      {showTable && (<DownloadTableExcel
                    filename="Inventory details"
                    sheet="Consolidated"
                    currentTableRef={con.current}
                >

                   <button> Export excel </button>

                </DownloadTableExcel>)}
           

      </>


  );
};

export default Inventory;
